        public unsafe static void AlternanteEquipItem(Client.GameClient client, ServerSockets.Packet stream, uint id, uint dwParam1, uint timestamp, uint dwParam2, uint dwParam3, uint dwparam4)
        {

            uint Position = dwParam1;
            Position -= 20;
            Game.MsgServer.MsgGameItem item = null;
            if (client.Inventory.TryGetItem(id, out item))
            {
                if (Database.ItemType.IsPistol(item.ITEM_ID))
                {
                    if (client.Equipment.FreeEquip(Role.Flags.ConquerItem.RightWeapon))
                        return;
                    Game.MsgServer.MsgGameItem rapier;
                    if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.RightWeapon, out rapier))
                    {
                        if (!Database.ItemType.IsRapier(rapier.ITEM_ID))
                            return;
                    }
                    else
                        return;
                }
                if (Position == (ushort)Role.Flags.ConquerItem.LeftWeapon)
                {
                    if (Database.ItemType.IsKnife(client.Equipment.RightWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID) == false)
                        {
                            client.SendSysMesage("Remove the right-hand Knife.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                }
                if (Position == (ushort)Role.Flags.ConquerItem.RightWeapon)
                {
                    if (Database.ItemType.IsKnife(client.Equipment.LeftWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID) == false)
                        {
                            client.SendSysMesage("Remove the left-hand Knife.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                    if (Database.ItemType.IsShield(client.Equipment.LeftWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID))
                        {
                            client.SendSysMesage("Remove the left-hand Shield.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                }
                bool can2hand = false;
                bool can2wpn = false;
                if (client.Player.Class >= 11 && client.Player.Class <= 85)
                    can2hand = true;
                if (client.Player.Class >= 11 && client.Player.Class <= 15 || client.Player.Class >= 20 && client.Player.Class <= 25 || client.Player.Class >= 51 && client.Player.Class <= 85)
                    can2wpn = true;

                if (client.Player.Class >= 40 && client.Player.Class <= 45)
                {
                    if (Database.ItemType.IsTwoHand(item.ITEM_ID) && client.Player.IsAsasin)
                        return;
                    //  if (Database.ItemType.IsKnife(item.ITEM_ID))
                    can2wpn = true;
                }
                if (!Database.ItemType.Equipable(item, client))
                    return;
                ushort po = Database.ItemType.ItemPosition(item.ITEM_ID);
                if (po == (ushort)Role.Flags.ConquerItem.SteedMount)
                    return;
                if (po == 5)
                {
                    Position = 5;
                    if (!can2hand && !can2wpn)
                        return;
                    if (!client.Equipment.FreeEquip(Role.Flags.ConquerItem.AleternanteRightWeapon))
                    {
                        Game.MsgServer.MsgGameItem RightWeapon;
                        if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.AleternanteRightWeapon, out RightWeapon))
                        {
                            if (RightWeapon.ITEM_ID / 1000 != 500 && Database.ItemType.IsArrow(item.ITEM_ID))
                                return;
                        }
                    }
                    else
                        return;
                }
                else if (po == 0) return;
                if (Database.ItemType.ItemPosition(item.ITEM_ID) == 4)
                {
                    if (Position == 5)
                        if (!can2hand || !can2wpn)
                            return;
                }
                if (!((Position == 4 || Position == 5) && (Database.ItemType.ItemPosition(item.ITEM_ID) == 4 || Database.ItemType.ItemPosition(item.ITEM_ID) == 5)))
                {
                    if (!Database.ItemType.IsAccessory(item.ITEM_ID))
                        Position = Database.ItemType.ItemPosition(item.ITEM_ID);
                }
                bool twohand = Database.ItemType.IsTwoHand(item.ITEM_ID);
                if (!twohand && Position == 4)
                {
                    Game.MsgServer.MsgGameItem LeftWeapon;
                    if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.AleternanteLeftWeapon, out LeftWeapon))
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            if (Database.ItemType.IsArrow(LeftWeapon.ITEM_ID))
                                client.Equipment.Remove(Role.Flags.ConquerItem.AleternanteLeftWeapon, stream);
                            else
                            {
                                Game.MsgServer.MsgGameItem RightWeapon;
                                if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.AleternanteRightWeapon, out RightWeapon))
                                {
                                    if (Database.ItemType.IsTwoHand(RightWeapon.ITEM_ID))
                                        client.Equipment.Remove(Role.Flags.ConquerItem.AleternanteRightWeapon, stream);
                                }
                            }
                        }
                    }
                }
                Position += 20;
                if (client.Equipment.FreeEquip((Role.Flags.ConquerItem)Position))
                {
                    bool spaceinventory = true;
                    if (twohand)
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            client.Equipment.Remove(Role.Flags.ConquerItem.AleternanteLeftWeapon, stream);
                        }
                        else
                        {
                            spaceinventory = false;
                        }
                    }
                    if (spaceinventory)
                    {
                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                        item.Position = (ushort)Position;
                        client.Equipment.Add(item, stream);
                        item.Mode = Role.Flags.ItemMode.Update;
                        item.Send(client, stream);
                    }
                }
                else//if is item on character
                {
                    bool spaceinventory = true;
                    if (twohand)
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            client.Equipment.Remove(Role.Flags.ConquerItem.AleternanteLeftWeapon, stream);
                        }
                        else
                            spaceinventory = false;
                    }
                    if (spaceinventory)
                    {
                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                        item.Position = (ushort)Position;
                        item.Mode = Role.Flags.ItemMode.AddItem;
                        //item.Send(client);
                        client.Equipment.Remove((Role.Flags.ConquerItem)Position, stream);
                        client.Equipment.Add(item, stream);
                    }
                }

                Game.MsgServer.MsgShowEquipment equip = new MsgShowEquipment();
                equip.wParam = item.UID;
                equip.Alternante = item.Position;
                equip.UID = 5;
                client.Send(stream.ShowEquipmentCreate(equip));
                if (client.Equipment.Alternante)
                {
                    client.Equipment.QueryEquipment(true);
                }
                else
                    client.Equipment.SendAlowAlternante(stream);
            }
        }
        public unsafe static void EquipItem(Client.GameClient client, ServerSockets.Packet stream, uint id, uint dwParam1, uint timestamp, uint dwParam2, uint dwParam3, uint dwparam4)
        {
            uint Position = dwParam1;

            Game.MsgServer.MsgGameItem item = null;
            if (client.Inventory.TryGetItem(id, out item))
            {

                switch (item.ITEM_ID)
                {
                    case 1200000:
                    case 1200001:
                    case 1200002:
                        {
                            UseItem(item, client, stream);
                            return;
                        }
                    default:
                        {
                            if (Position == 17 && Database.ItemType.ItemPosition(item.ITEM_ID) == 0)
                            {
                                UseItem(item, client, stream);
                                return;
                            }
                            else if (Position == 0 && Database.ItemType.ItemPosition(item.ITEM_ID) == 0)
                            {
                                UseItem(item, client, stream);
                                return;
                            }
                            break;
                        }
                }

                if (client.Player.ContainFlag(MsgUpdate.Flags.PathOfShadow))
                {
#if Arabic
                      client.SendSysMesage("Please remove you PathOfShadow.");
#else
                    client.SendSysMesage("Please remove you PathOfShadow.");
#endif

                    return;
                }
                if (Database.ItemType.ItemPosition(item.ITEM_ID) == (ushort)Role.Flags.ConquerItem.Garment)
                {
                    if (client.Player.SpecialGarment != 0)
                    {
#if Arabic
                           client.SendSysMesage("Item can't be unequiped during the event.");
#else
                        client.SendSysMesage("Item can't be unequiped during the event.");
#endif

                        return;
                    }
                }

                if (Database.ItemType.IsPistol(item.ITEM_ID))
                {
                    if (client.Equipment.FreeEquip(Role.Flags.ConquerItem.RightWeapon))
                        return;
                    Game.MsgServer.MsgGameItem rapier;
                    if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.RightWeapon, out rapier))
                    {
                        if (!Database.ItemType.IsRapier(rapier.ITEM_ID))
                            return;
                    }
                    else
                        return;
                }


                bool can2hand = false;
                bool can2wpn = false;
                if (client.Player.Class >= 11 && client.Player.Class <= 85 || client.Player.Class >= 160 && client.Player.Class <= 165)
                    can2hand = true;
                if (client.Player.Class >= 11 && client.Player.Class <= 15 || client.Player.Class >= 20 && client.Player.Class <= 25 || client.Player.Class >= 51 && client.Player.Class <= 145
                    || client.Player.Class >= 160 && client.Player.Class <= 165)
                    can2wpn = true;

                if (client.Player.Class >= 40 && client.Player.Class <= 45)
                {
                    if (Database.ItemType.IsTwoHand(item.ITEM_ID) && client.Player.IsAsasin)
                        return;
                    // if (Database.ItemType.IsKnife(item.ITEM_ID))
                    can2wpn = true;
                }
                if (!Database.ItemType.Equipable(item, client))
                    return;
                ushort po = Database.ItemType.ItemPosition(item.ITEM_ID);
                if (Database.AtributesStatus.IsTaoist(client.Player.Class))
                {
                    if (po == (ushort)Role.Flags.ConquerItem.LeftWeapon)
                    {
                        if (Database.ItemType.IsHossu(item.ITEM_ID) == false)
                            return;
                    }
                }
                if (Position == 5)
                {
                    if (Database.ItemType.IsTwoHand(client.Equipment.RightWeapon))
                    {
                        if (Database.ItemType.IsShield(item.ITEM_ID) == false)
                            return;
                    }
                }
                if (Database.ItemType.IsShield(item.ITEM_ID) && !(client.Player.Class >= 21 && client.Player.Class <= 25))
                    return;
                if (Position == 5)
                {
                    bool isPrevArch = (client.Player.Reborn > 0 && (client.Player.FirstClass == 45 || client.Player.SecoundeClass == 45));

                    if (!(Database.AtributesStatus.IsArcher(client.Player.Class)) && !isPrevArch)
                    {
                        if (Database.ItemType.IsTwoHand(client.Equipment.RightWeapon))
                        {
                            if (Database.ItemType.IsShield(item.ITEM_ID) == false)
                                client.SendSysMesage("Remove the left-hand Shield.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                }
                if (Position == (ushort)Role.Flags.ConquerItem.LeftWeapon)
                {
                    if (Database.ItemType.IsKnife(client.Equipment.RightWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID) == false)
                        {
                            client.SendSysMesage("Remove the right-hand Knife.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                }
                if (Database.ItemType.IsBow(item.ITEM_ID) && !(client.Player.Class >= 40 && client.Player.Class <= 45))
                {
                    client.SendSysMesage("You have to be archer to used bow.", MsgMessage.ChatMode.System);
                    return;
                }
                if (Database.ItemType.IsBacksword(item.ITEM_ID) && !(client.Player.Class >= 100 && client.Player.Class <= 145))
                {
                    client.SendSysMesage("You have to be Taoist to used Backsword.", MsgMessage.ChatMode.System);
                    return;
                }
                if (Database.ItemType.IsShield(item.ITEM_ID) && !(client.Player.Class >= 20 && client.Player.Class <= 25))
                {
                    client.SendSysMesage("You have to be Warrior to used Shield.", MsgMessage.ChatMode.System);
                    return;
                }
                if (Position == (ushort)Role.Flags.ConquerItem.RightWeapon)
                {
                    if (Database.ItemType.IsKnife(client.Equipment.LeftWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID) == false)
                        {
                            client.SendSysMesage("Remove the left-hand Knife.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                    if (Database.ItemType.IsShield(client.Equipment.LeftWeapon))
                    {
                        if (Database.ItemType.IsKnife(item.ITEM_ID))
                        {
                            client.SendSysMesage("Remove the left-hand Shield.", MsgMessage.ChatMode.System);
                            return;
                        }
                    }
                }

                if (po == (ushort)Role.Flags.ConquerItem.Garment || po == (ushort)Role.Flags.ConquerItem.SteedMount)
                    return;
                if (po == 5)
                {
                    Position = 5;
                    if (!can2hand && !can2wpn)
                        return;
                    if (!client.Equipment.FreeEquip(Role.Flags.ConquerItem.RightWeapon))
                    {
                        Game.MsgServer.MsgGameItem RightWeapon;
                        if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.RightWeapon, out RightWeapon))
                        {
                            if (RightWeapon.ITEM_ID / 1000 != 500 && Database.ItemType.IsArrow(item.ITEM_ID))
                                return;
                        }
                    }
                    else
                        return;
                }
                else if (po == 0) return;
                if (Database.ItemType.ItemPosition(item.ITEM_ID) == 4)
                {
                    if (Position == 5)
                        if (!can2hand || !can2wpn)
                            return;
                }
                if (!((Position == 4 || Position == 5) && (Database.ItemType.ItemPosition(item.ITEM_ID) == 4 || Database.ItemType.ItemPosition(item.ITEM_ID) == 5)))
                {
                    if (!Database.ItemType.IsAccessory(item.ITEM_ID))
                        Position = Database.ItemType.ItemPosition(item.ITEM_ID);
                }
                bool twohand = Database.ItemType.IsTwoHand(item.ITEM_ID);
                if (!twohand && Position == 4)
                {
                    Game.MsgServer.MsgGameItem LeftWeapon;
                    if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.LeftWeapon, out LeftWeapon))
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            if (Database.ItemType.IsArrow(LeftWeapon.ITEM_ID))
                                client.Equipment.Remove(Role.Flags.ConquerItem.LeftWeapon, stream);
                            else
                            {
                                Game.MsgServer.MsgGameItem RightWeapon;
                                if (client.Equipment.TryGetEquip(Role.Flags.ConquerItem.RightWeapon, out RightWeapon))
                                {
                                    if (Database.ItemType.IsTwoHand(RightWeapon.ITEM_ID))
                                        client.Equipment.Remove(Role.Flags.ConquerItem.RightWeapon, stream);
                                }
                            }
                        }
                    }
                }
                if (client.Equipment.FreeEquip((Role.Flags.ConquerItem)Position))
                {
                    bool spaceinventory = true;
                    if (twohand)
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            client.Equipment.Remove(Role.Flags.ConquerItem.LeftWeapon, stream);
                        }
                        else
                        {
                            spaceinventory = false;
                        }
                    }
                    if (spaceinventory)
                    {
                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                        item.Position = (ushort)Position;
                        client.Equipment.Add(item, stream);
                        item.Mode = Role.Flags.ItemMode.Update;
                        item.Send(client, stream);
                    }
                }
                else//if is item on character
                {
                    bool spaceinventory = true;
                    if (twohand)
                    {
                        if (client.Inventory.HaveSpace(1))
                        {
                            client.Equipment.Remove(Role.Flags.ConquerItem.LeftWeapon, stream);
                        }
                        else
                            spaceinventory = false;
                    }
                    if (spaceinventory)
                    {
                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                        item.Position = (ushort)Position;
                        item.Mode = Role.Flags.ItemMode.AddItem;
                        //item.Send(client);
                        client.Equipment.Remove((Role.Flags.ConquerItem)Position, stream);
                        client.Equipment.Add(item, stream);
                    }
                }
                client.Equipment.QueryEquipment(client.Equipment.Alternante);
            }
        }



        public static void UseItem(Game.MsgServer.MsgGameItem item, Client.GameClient client, ServerSockets.Packet stream)
        {
            if (!client.Player.Alive)
                return;

            Database.ItemType.DBItem DBItem;
            if (Database.Server.ItemsBase.TryGetValue(item.ITEM_ID, out DBItem))
            {
                if (client.PipeServer != null)
                {
                    if (item.ITEM_ID >= 1000000 && item.ITEM_ID <= 1000040
                  || item.ITEM_ID >= 1002000 && item.ITEM_ID <= 1002030
                  || item.ITEM_ID == 1002050
                  || item.ITEM_ID == 725065 || item.ITEM_ID == 1003010)
                    {
                        if (DateTime.Now > client.Player.MedicineStamp.AddMilliseconds(500))
                        {
                            if (client.Player.ContainFlag(MsgUpdate.Flags.PoisonStar))
                                return;
                            if (client.EventBase != null && client.EventBase.PotionsAllowed == false && client.EventBase.Stage == MsgEvents.EventStage.Fighting)
                                return;
                            if (client.Player.HitPoints == client.Status.MaxHitpoints)
                                return;
                            client.Player.HitPoints = Math.Min(client.Player.HitPoints + DBItem.ItemHP, (int)client.Status.MaxHitpoints);
                            client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                            client.Player.MedicineStamp = DateTime.Now;
                        }
                    }
                    else if (item.ITEM_ID >= 1001000 && item.ITEM_ID <= 1001040
                        || item.ITEM_ID == 1002030 || item.ITEM_ID == 1002040
                        || item.ITEM_ID == 725066 || item.ITEM_ID == 1004010)
                    {
                        if (client.Player.ContainFlag(MsgUpdate.Flags.PoisonStar))
                            return;

                        if (client.Player.Mana == client.Status.MaxMana)
                            return;
                        client.Player.Mana = (ushort)Math.Min(client.Player.Mana + DBItem.ItemMP, (int)client.Status.MaxMana);
                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                    }
                    return;
                }
                else if (client.IsConnectedInterServer())
                {
                    return;
                }
                #region House items
                if (item.ITEM_ID >= 726001 && item.ITEM_ID <= 726108 || item.ITEM_ID == 722559// item box.
                || item.ITEM_ID >= 721177 && item.ITEM_ID <= 721202)
                {
                    if (Database.HouseTable.InHouse(client.Player.Map) && client.Player.DynamicID == client.Player.UID/*that check if is you house*/)
                    {
                        NpcServerQuery Furniture = new NpcServerQuery();
                        if (item.ITEM_ID == 722559)
                            Furniture.NpcType = Role.Flags.NpcType.Talker;
                        else
                            Furniture.NpcType = Role.Flags.NpcType.Furniture;

                        Furniture.Action = MsgNpc.NpcServerReplay.Mode.Cursor;
                        var Npc = Database.NpcServer.GetNpc(item.ITEM_ID);
                        if (Npc != null)
                        {
                            client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);

                            Furniture.Mesh = Npc.Mesh;
                            Furniture.ID = (MsgNpc.NpcID)Npc.UID;
                            client.MoveNpcMesh = Furniture.Mesh;
                            client.MoveNpcUID = (uint)Furniture.ID;
                            client.Send(stream.NpcServerCreate(Furniture));
                        }
                    }
                    else
                    {
#if Arabic
                          client.SendSysMesage("You have to be in your own house to be able to display it.", MsgMessage.ChatMode.System, MsgMessage.MsgColor.red);
#else
                        client.SendSysMesage("You have to be in your own house to be able to display it.", MsgMessage.ChatMode.System, MsgMessage.MsgColor.red);
#endif

                    }
                    return;
                }
                #endregion
                #region EquipmentPack

                if (item.ITEM_ID == 3003219)//RecruitGearBox
                {
                    if (client.Player.JustCreated)
                    {
                        uint[] IDs = null;
                        switch (client.Player.Class)
                        {
                            case 10:
                                int rand = new Random().Next(1, 4);
                                uint[] Ids = null;
                                switch (rand)
                                {
                                    case 1:
                                        Ids = new uint[2] { 420029/*sword*/, 410029/*blade*/ };
                                        break;
                                    case 2:
                                        Ids = new uint[2] { 420029/*sword*/, 480029/*club*/ };
                                        break;
                                    case 3:
                                        Ids = new uint[2] { 410029/*blade*/, 480029/*club*/ };
                                        break;
                                }
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, Ids[0], Ids[1], 130009/*Armor*/, 118009/*hood*/ };
                                break;
                            case 20:
                                int rands = new Random().Next(1, 3);
                                uint ID = 0;
                                switch (rands)
                                {
                                    case 1:
                                        ID = 561029;
                                        break;
                                    case 2:
                                        ID = 560029;
                                        break;
                                }
                                IDs = new uint[6] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, ID/*weap*/, 131009/*Armor*/, 111009/*hood*/ };
                                break;
                            case 40:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 613019/*knife*/, 613019/*knife*/, 133009/*Armor*/, 113009/*hood*/ };
                                break;
                            case 50:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 601029/*katana*/, 601029/*katana*/, 135009/*Armor*/, 123009/*hood*/ };
                                break;
                            case 60:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 610029/*prayer*/, 610029/*prayer*/, 136009/*Armor*/, 143009/*hood*/ };
                                break;
                            case 70:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 611029/*raper*/, 612029/*pistol*/, 139009/*Armor*/, 144009/*hood*/ };
                                break;
                            case 80:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 617029/*weap*/, 617029/*weap*/, 138009/*Armor*/, 148009/*hood*/ };
                                break;
                            case 100:
                                IDs = new uint[6] { 152019/*necklace*/, 121029/*ring*/, 160039/*boot*/, 421029/*weap*/, 134009/*Armor*/, 114009/*hood*/ };
                                break;
                            case 160:
                                IDs = new uint[7] { 120029/*necklace*/, 150039/*ring*/, 160039/*boot*/, 626029/*weap*/, 626029/*weap*/, 101009/*Armor*/, 170009/*hood*/ };
                                break;
