                        client.Inventory.Update(item, Role.Instance.AddMode.REMOVE, stream);
                    }
                }
                #endregion


                #region Kujiku

                #region NewbieToken
                if (item.ITEM_ID == 722122)
                {
                    if (DateTime.Now < client.Player.LastNewbieUsed.AddMinutes(1))
                    {
                        client.SendSysMesage("You need to wait at least 1 minute to use it again!");
                        return;
                    }
                    else if (client.Team != null)
                    {
                        client.SendSysMesage("You are aleady in a Team!");
                        return;
                    }
                    else if (client.Player.Level >= 100)
                    {
                        client.SendSysMesage("You can't use me again after level 100, It have disappear.");
                        client.Inventory.Remove(722122, 1, stream);
                        return;
                    }
                    else
                    {
                        client.Player.LastNewbieUsed = DateTime.Now;
                        client.Player.SendGlobalMesage($"{client.Player.Name} is looking for strong players to help him/her out leveling, please contact him/her!", MsgMessage.ChatMode.TopLeft);
                        foreach (var player in Database.Server.GamePoll.Values)
