                            if (player == client)
                                continue;
                            if (player.Team == null)
                                continue;

                            if (player?.Team?.Leader == player && player?.Player.Level > 120 && player.Team.CkeckToAdd() && client?.Team == null && player.Player.Class >= 40)
                            {
                                player.Player.MessageBox($"{client.Player.Name} is looking for strong player to help him/her out leveling?", new Action<Client.GameClient>(p => p.Team.AddPlayer(stream, client, p.Player)), null, 60, MsgStaticMessage.Messages.None);
